#include <iostream>
#include <string>
#include <cmath>

using namespace std;

void printCharArray(int a[])
{
	char j = 'a';

	for (int i = 0; i < 26; i++, j++)
		cout << left << j << "  ";
	cout << endl; 

	for (int i = 0; i < 26; i++)
		cout << left << a[i] << "  ";
	cout << endl << endl;
}

int number_needed(string a, string b)
{
	int alphaA[26] = {0};
	int sum = 0;

	for (int i = 0; i < a.length(); i++)
		alphaA[a[i]-'a']++;

	printCharArray(alphaA);

	for (int i = 0; i < b.length(); i++)
		alphaA[b[i]-'a']--;

	printCharArray(alphaA);

	for (int i = 0; i < 26; i++)
		sum += abs(alphaA[i]);

	return sum;
}

int main()
{	
	string x = "fcrxzwscanmligyxyvym";
	string y = "jxwtrhvujlmrpdoqbisbwhmgpmeoke";

	//string x = "aaaaaaaaaaaaaaaaaaaaaaaaaaaaa";
    //string y = "bbbbbbbbbbbbbbbbbbbbbbbbbbbbbbbbbbbbbbbbbbbbbbbbbbbbbbbbbbbbbbbbbbbbbbbbbbb";

	//string x = "";
	//string y = "";

	//string x = "abc";
	//string y = "cde";

	cout << "\nNumber of characters needed to be removed from "
	     << x << " and " << y << " = " << number_needed(x,y) << endl << endl;

	return 0;
}